INSERT INTO `wp_posts` VALUES (39, 2, '2010-02-24 19:00:54', '0000-00-00 00:00:00', 'ddafa sssdfdsfsdfsfs sdfsdfsfsdf', '', '', 'draft', 'open', 'open', '', '', '', '', '2010-02-24 19:00:54', '0000-00-00 00:00:00', '', 0, 'http://alzheimerinisrael.com/blog/?p=39', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES (40, 1, '2010-02-22 11:23:30', '2010-02-22 09:23:30', 'מחלת האלצהיימר מתבטאת פעמים רבות בתוקפנות, אלימות פיזית, התפרצויות מילוליות ואגרסיה. ביטויים שליליים אלה נגרמים כתוצאה משינויים כימיים ופיזיולוגיים המתרחשים במוחו של האדם החולה. במהלך שנות עבודה רבות עם חולי אלצהיימר נתקלתי לא פעם בביטויים הפוכים ומפתיעים. אצל חלק מהחולים, השינויים הנחרטים במוח עקב האלצהיימר מולידים יכולות יצירה שלא היו קיימות בו כלל קודם, כמו גם שינויים לטובה באופיו של האדם.\r\n\r\nתמונה זו התבהרה לי היטב כאשר צפיתי בתוכנית על המוח האנושי בערוץ 8. התוכנית הדגימה כיצד אותן תהפוכות החלות במוח האנושי בעת המחלה עשויות לברוא אצל החולה פן אמנותי שלא היווה חלק ממנו עד שלקה במחלה. הסבר זה התקשר למקרה בו נתקלתי בפועל במהלך עבודתי:\r\n\r\nלמרכז יום לחולי אלצהיימר בו עבדתי הגיע, מלווה באשתו, אדם כבן 75. אותו אדם ידע לשרוק בדרך יוצאת דופן יצירות מוסיקליות מורכבות. נהגתי לשבת ליד הפסנתר ולנגן יצירות קלאסיות, שככל הנראה את רובן כלל לא הכיר, והוא היה מצטרף אליי מיד בשריקה מדויקת ומקסימה. הוא ללא ספק הפגין מוסיקליות בלתי רגילה.\r\n\r\nכאשר שאלתי את אשתו האם במהלך חייו אהב לשמוע מוסיקה, האם ניגן או היה מוסיקלי באופן בולט – השיבה כי מעולם במהלך חייו לפני המחלה לא עסק במוסיקה בשום צורה.\r\n\r\nבמקרים לא מעטים, הוכח שמחלת האלצהיימר גורמת לאנשים מסוימים פרץ של יצירתיות שלא היה בהם קודם – נגינה, ציור ועוד. תופעה נוספת ומוכרת, שאף היא הוצגה והוסברה בתוכנית – מתייחסת לפן נוסף הכרוך בשינוי אישיותי. ישנם <span style="text-decoration: underline;">מקרים </span> בהם אנשים אשר במהלך חייהם היו קשים ולא נחמדים לסביבה – הפכו עקב המחלה לסימפטיים וחביבים הרבה יותר.\r\n\r\nמחלת ה<span style="text-decoration: underline;">אלצהיימר</span> נקשרת, ובצדק, להיבטים כואבים וקשים, אך כדאי לדעת להבחין בנקודות האור שהיא מציפה לא פעם מעל לפני השטח. טיפול תרופתי מתאים, הכוונה לטיפול התנהגותי ופיזי הולם לחולה והדרכה נכונה לבני המשפחה, מסייעים רבות ומקדמים עבודה עם מה שקיים ונותר אצל האדם החולה, ובכך מקלים עליו ועל סביבתו.\r\n\r\nשלכם,\r\n\r\nאורית שביט, מומחית לאלצהיימר\r\n\r\nהכוונה וליווי למשפחות של חולי אלצהיימר\r\n<a href="http://www.alzheimerinisrael.com/">http://www.alzheimerinisrael.com/</a>\r\n\r\n054-2277801', 'מה בין מחלת האלצהיימר ליצירתיות?', '', 'inherit', 'open', 'open', '', '36-revision-2', '', '', '2010-02-22 11:23:30', '2010-02-22 09:23:30', '', 36, 'http://alzheimerinisrael.com/blog/36-revision-2/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (41, 1, '2010-02-16 18:18:46', '2010-02-16 16:18:46', 'כשמישהו מבני המשפחה לוקה במחלת האלצהיימר, יכולה משפחתו לזכות בהתחדשות נפלאה. נשמע תמוה? אכן. כאשר אני נתקלת בגילוי זה לא פעם בעבודתי, כמלווה ומדריכה משפחות של חולי אלצהיימר ודמנציה, אני מתמלאת שמחה וכוח. אותן משפחות זוכות בהישגים יקרי ערך, דווקא מהמקום הכאוב בו הן נמצאות. בגללו או שמא – בזכותו.\r\n\r\nלאחרונה ליוויתי בת לאם חולת אלצהיימר, שמצאה את עצמה לבדה במערכה בהתמודדות עם מחלת אימה. חלק משמעותי בעבודת ההדרכה איתה ועם בני המשפחה עסק במאמץ לחלק את הנטל בין כולם. כל אחד מבני המשפחה לקח על עצמו תפקיד הקשור בטיפול באמא, המשפחה התגייסה, וניתן לומר כי המחלה חיברה בין הקצוות ואיחדה אותם מחדש.\r\n\r\nתוצר נוסף נפלא שהתרחש אצל אותה משפחה קשור בבת שנשאה בעיקר העול. דווקא בנקודה קשה זו בחייה, היא זכתה לעדנה מחודשת ביחסים הזוגיים שלה. מחלת האלצהיימר של אימה וההתמודדות עם הקשיים והכאב, פתחו פתח ליצירת מערכת יחסים עם בעלה כפי שלא חוו קודם לכן. אני מלאת הערכה לאישה זו ולמשפחתה, שידעו לגייס את מלוא הכוחות למען אימם והצליחו להפיק מההתמודדות תהליכים אחרים חשובים ומשמעותיים עבורם.\r\n\r\nיש בתהליך הטיפול בחולי אלצהיימר, כוחות מעצימים ומחזקים לקרובים ולבני המשפחה. כפי שכתבה לי מיכל, איתה ועם בני משפחתה עברנו תהליך יעוץ והכוונה עמוק, פורה ומשמעותי לאחר שאימה חלתה באלצהיימר.\r\nהיי אורית יקרה -\r\n\r\nשמחתי והתעצמתי ממפגש הסיכום והפרידה שלנו.\r\n\r\nתודה רבה ,על שהצלחת בדרכך המיוחדת לעזור ובמיוחד להקשיב בסבלנות אין קץ, במקצועיות וברצינות האופיינית לך.\r\n\r\nכבר בשיחת טלפון ראשונה, הרגשתי תמיכה, הכלה מאישה רגישה, בטוחה ושלווה.\r\n\r\nהקנית לי ולבני משפחתי הקרובה כלים להבין<strong> </strong>ולהתמודד. כתוצאה מהשינוי, פחת הסבל והכאב, והמפגשים עם אמא והמחלה הארורה, הפכו פחות מאיימים וטעונים.\r\n\r\nאני ממליצה על ההכוונה והייעוץ שלך לכל משפחה שמתמודדת כבר בתחילתו של זיהוי המחלה ההרסנית.\r\n\r\nנמשיך את הקשר שלנו. כפי שכתבת לי -  לבבות לא נפרדים.\r\n\r\nחיבוק גדול לתודה ענקית.\r\n\r\nבברכה,\r\n\r\nמיכל רוזן\r\nמאמנת מומחית להעצמה אישית נשית\r\n\r\nמשפחות יקרות, יש להיתמך בטיפול בחולה אלצהיימר, הן בהדרכה יומיומית – תפקודית, התנהגותית וטיפולית והן – בתמיכה רגשית עבורכם. התהליך הוא קשה ומורכב, אך בדרך מודעת ונכונה – ניתן לצמוח ממנו.\r\n\r\nשלכם,\r\n\r\nאורית שביט, מומחית לאלצהיימר\r\n\r\nהכוונה וליווי למשפחות של חולי אלצהיימר\r\n<a href="http://www.alzheimerinisrael.com/">http://www.alzheimerinisrael.com/</a>\r\n\r\n054-2277801', 'צמיחה משפחתית בצל האלצהיימר', '', 'inherit', 'open', 'open', '', '34-revision-2', '', '', '2010-02-16 18:18:46', '2010-02-16 16:18:46', '', 34, 'http://alzheimerinisrael.com/blog/34-revision-2/', 0, 'revision', '', 0);
