INSERT INTO `wp_posts` VALUES (48, 1, '2010-03-16 11:22:04', '2010-03-16 09:22:04', 'משפחות של חולי אלצהיימר עלולות להיתקל בתופעות שונות ומשונות אצל יקיריהם. כולן מחייבות תשומת לב מיוחדת והתייחסות זהירה על מנת לשמור על בטיחותו של האדם החולה, כולל הגנה על כספו ורכושו.\r\n\r\nלמה הכוונה? נתקלתי במשפחות של אנשים הלוקים בדמנציה או אלצהיימר שנדהמות לגלות כי החלו לפתע להוציא כספים בצורה פזרנית ולא אחראית. תופעה זו עלולה לאפיין חולי אלצהיימר המאבדים את יכולתם להבחין במצבים בהם מנסים למכור להם דברים בעורמה, שעובדים עליהם. כך למשל, משפחה לאישה חולת אלצהיימר אותה ליוויתי, גילתה כי היא התחילה לאגור מוצרי חשמל בביתה, מילאה חדר שלם במוצרי חשמל יקרים ומיותרים. אותה אישה נהגה לרכוש את המוצרים דרך הטלפון מכל מי שהציע לה.\r\n\r\nלצערנו, ישנן חברות מסחריות המנצלות לצרכיהן את חוסר יכולתם של אותם אנשים להפעיל שיקול דעת וכלים שיפוטיים במצבים כאלה.\r\n\r\nאני פונה אליכם - משפחות יקרות, ומציעה לכם – נהלו מעקב שוטף אחרי הוצאותיו של אותו אדם, בדקו את כרטיסי האשראי שלו, שוחחו עם הפקידים בבנק שלו על מנת שיבדקו כל פעולה חריגה שיבקש לבצע וימנעו במידת האפשר את הוצאתה לפועל, עשו את הפעולות הנדרשות מול הגורמים השונים כדי למזער נזקים קשים שעלולים להיווצר.\r\n\r\nטיפול בחולי אלצהיימר הינו יומיומי ומורכב וההתמודדות היא קשה. יש להתאים את הסביבה הפיזית לצורך הבטחת בטיחותו של החולה, לדעת איך לתקשר איתו, להעניק לו עזרה בתפקוד השוטף, להקל על בעיות התנהגותיות ולהיטיב את הרגשתו באמצעות שיטות טיפול גופניות וחושיות. בתוך כל אלה, צריך לעקוב אחרי תופעות חריגות כפי שציינתי.\r\n\r\nבנוסף, קיימת כמובן ההתמודדות הרגשית מול האדם הקרוב ההולך ושוקע למחוזות של שכחה ובלבול. אני מציעה לכם להיתמך בניסיוני העשיר ובידע הרב שצברתי בתחום במשך שנים רבות כמלווה של חולי אלצהיימר ובני משפחתם. אל תהססו לפנות אלי.\r\n\r\nשלכם,\r\n\r\nאורית שביט, מומחית לאלצהיימר\r\n\r\nהכוונה וליווי למשפחות של חולי אלצהיימר\r\n<a href="http://www.alzheimerinisrael.com/">http://www.alzheimerinisrael.com/</a>\r\n\r\n054-2277801', 'מה עושים כשחולה אלצהיימר משתמש בחוסר אחריות בכספו?', '', 'publish', 'open', 'open', '', '%d7%9e%d7%94-%d7%a2%d7%95%d7%a9%d7%99%d7%9d-%d7%9b%d7%a9%d7%97%d7%95%d7%9c%d7%94-%d7%90%d7%9c%d7%a6%d7%94%d7%99%d7%99%d7%9e%d7%a8-%d7%9e%d7%a9%d7%aa%d7%9e%d7%a9-%d7%91%d7%97%d7%95%d7%a1%d7%a8-%d7%90', '', '', '2010-03-16 11:22:04', '2010-03-16 09:22:04', '', 0, 'http://alzheimerinisrael.com/blog/?p=48', 0, 'post', '', 0);
