INSERT INTO `wp_posts` VALUES (112, 1, '2010-09-13 16:52:38', '2010-09-13 14:52:38', 'איך <em><strong>תוכנית תעסוקה</strong></em> מותאמת אישית יכולה לעזור לחולה אלצהיימר? כיצד יכולה סביבה מטפלת רגישה וקשובה לתת מענה ל<strong><em>תופעות קיצוניות</em></strong> הכרוכות במחלה?\r\n\r\nכל שלב במחלת <span style="text-decoration: underline;">האלצהיימר</span> מלווה בסימפטומים ומאפיינים שונים. כל אדם מתנהג ומגיב קצת אחרת ל<strong><em>התפתחות המחלה</em></strong>. המשותף לכולם, או ליתר דיוק למרבית החולים, הוא שתעסוקה קוגניטיבית וגופנית המותאמת לצרכיו ולסדר יומו של החולה ובני ביתו – הכרחית וחיונית לאיכות חייו ולניהול המחלה.\r\n\r\nלאחרונה הוזמנתי להדריך משפחה המלווה איש מבוגר יחסית החולה באלצהיימר. האדם נמצא כבר ב<strong><em>שלב השלישי</em></strong> של המחלה כלומר - הוא <strong><em>סיעודי</em></strong> לגמרי וצריך עזרה בכל הפעולות היומיומיות. בנוסף, הוא וסביבתו סובלים מתופעה מטרידה - צעקות. האיש צועק כל הזמן. עד שלא הגעתי לביקור, היו בני משפחתו והעובד הזר שאיתו אובדי עצות ולא ידעו מה לעשות כדי לעצור את הצעקות.\r\n\r\nכאשר אני מגיעה ל<span style="text-decoration: underline;">ביתו</span> של <a href="http://www.alzheimerinisrael.com">חולה אלצהיימר</a>, אני אוספת נתונים רבים ככל האפשר על מצבו, אם באמצעות צפייה, שיחה איתו (במידה וזה אפשרי), שיחות עם המטפלים בו, בחינת התרופות שהוא מקבל ועוד...לאחר מכן אני מרכיבה לכל אדם תוכנית תעסוקה המתאימה לאדם המסוים הזה.\r\n\r\nגם במקרה המדובר פעלתי כך. הכנתי לאותו אדם תוכנית תעסוקה, וראה זה פלא – האיש הפסיק לצעוק! איך זה קרה? מה התרחש שם?\r\n\r\nבמהלך השיחה עם אשתו של החולה, ישב בצד העובד הזר שמטפל בו והאזין בשקט. הוא גילה עניין רב בדברים והתחיל לאתר יוזמות ודרכים כיצד לעזור. לאחר שראה אותי מקריאה סיפור לחולה, ואת החולה מאזין בשקט מוחלט, הוא חיפש ומצא בטלוויזיה ערוץ המשדר פולמוסים ודיונים בנושאי דת. כאשר החולה ישב והאזין לערוץ זה, הוא ישב והקשיב, האזין בשקט... בלי לצעוק בכלל. מידת ההבנה שלו כמובן מוטלת בספק, אבל משהו בהאזנה לדיבורים הללו – תפס אותו. האיש בא מבית דתי ויתכן שעולם התוכן משך אותו, חיבר אותו למקומות שהיו משמעותיים בעברו.\r\n\r\nבנוסף, רגישותו ותבונתו של העובד הזר הזה סייעו ביותר וחוללו נפלאות. בר מזל החולה שיש לו מטפל כמוהו.\r\n\r\nכמי שעוסקת ומתמחה שנים רבות בליווי והדרכה של משפחות לחולי אלצהיימר, אני ממליצה להסתייע בכל ידע מקצועי כדי להתמודד עם האתגרים הקשים שמציבה מחלת האלצהיימר. בארה"ב התמחייתי בשיטת טיפול באלצהיימר הנקראת <span style="text-decoration: underline;">Habilitation</span>, המדריכה משפחות של חולים כיצד לחיות <strong><em>חיים נורמאליים</em></strong> בבית לצד האדם החולה, בד בבד עם המאמץ לעבוד עם היכולות שנשארו אצלו. כדי להעניק לחולה טיפול טוב - חשוב להבין את <strong><em>תהליכי המחלה</em></strong>, להכיר מקרוב את רגשותיו ורצונותיו של החולה, ללמוד כיצד לתקשר איתו, איך להפעילו, כיצד להרכיב ולנהל את שגרת יומו.\r\n\r\nשלכם,\r\n\r\nאורית שביט, מומחית לאלצהיימר\r\n\r\nהכוונה וליווי למשפחות של חולי אלצהיימר\r\n<a href="http://www.alzheimerinisrael.com/">http://www.alzheimerinisrael.com/</a>\r\n\r\n054-2277801', 'במה מתעניין חולה אלצהיימר?', '', 'publish', 'open', 'open', '', '%d7%91%d7%9e%d7%94-%d7%9e%d7%aa%d7%a2%d7%a0%d7%99%d7%99%d7%9f-%d7%97%d7%95%d7%9c%d7%94-%d7%90%d7%9c%d7%a6%d7%94%d7%99%d7%99%d7%9e%d7%a8', '', '', '2010-09-13 18:50:09', '2010-09-13 16:50:09', '', 0, 'http://alzheimerinisrael.com/blog/?p=112', 0, 'post', '', 0);
